import { Link } from "react-router-dom";
import Button from "./Button.js";

export default function Product(props) {
  const { details } = props;

  const productFromCart = props.cart.find(
    (product) => product.id === details.id
  );
  const quantity = productFromCart ? productFromCart.quantity : 0;

  return (
    <div className="product">
      <div className="product-image-container">
        <Link to={`/products/${details.id}`}>
          <img
            src="https://image.baidu.com/search/detail?ct=503316480&z=0&ipn=d&word=%E6%B0%B4%E6%9E%9C%E5%9B%BE%E7%89%87&hs=0&pn=3&spn=0&di=7108135681917976577&pi=0&rn=1&tn=baiduimagedetail&is=0%2C0&ie=utf-8&oe=utf-8&cl=2&lm=-1&cs=707248498%2C3226991042&os=2170821506%2C62061691&simid=3565280918%2C690128029&adpicid=0&lpn=0&ln=30&fr=ala&fm=&sme=&cg=&bdtype=0&oriquery=%E6%B0%B4%E6%9E%9C%E5%9B%BE%E7%89%87&objurl=https%3A%2F%2Fgimg2.baidu.com%2Fimage_search%2Fsrc%3Dhttp%3A%2F%2Ftu1.whhost.net%2Fuploads%2F20181016%2F15%2F1539675060-dslgPXGcOR.jpg%26refer%3Dhttp%3A%2F%2Ftu1.whhost.net%26app%3D2002%26size%3Df9999%2C10000%26q%3Da80%26n%3D0%26g%3D0n%26fmt%3Dauto%3Fsec%3D1660723993%26t%3D2877ba1bd4a83403af4f6485ee8e77a1&fromurl=ippr_z2C%24qAzdH3FAzdH3Fooo_z%26e3Boii5fp_z%26e3BgjpAzdH3Ffi7t275AzdH3Fnc8d8_z%26e3Bip4s&gsm=4&islist=&querylist=&dyTabStr=MCwzLDYsNCw1LDEsMiw4LDcsOQ%3D%3D"
            width="100"
            height="100"
            className="product-image"
            alt={details.name}
          />
        </Link>
        {quantity > 0 && (
          <div className="product-quantity-container">
            <div className="product-quantity">{quantity}</div>
          </div>
        )}
      </div>
      <div className="product-info">
        <h3>{details.name}</h3>
        <p>{details.description}</p>
      </div>
      <div className="product-checkout">
        <div>
          {quantity > 0 && (
            <Button
              outline
              onClick={() => props.onProductDelete(details.id)}
              className="product-delete"
            >
              x
            </Button>
          )}
        </div>
        <Button outline onClick={() => props.onProductAdd(details)}>
          ${details.price}
        </Button>
      </div>
    </div>
  );
}
